import urllib
from bs4 import BeautifulSoup
import chardet
from urllib import request
url = "https://abcnews.go.com/"
response = request.urlopen(url, timeout=10)
html = response.read()
charset = chardet.detect(html)
html = html.decode(str(charset["encoding"]))
soup = BeautifulSoup(html, "html.parser")
# headlines-li-div -> href=url data-analytics="cid=clicksource_4380645_2_heads_hero_live_headlines_hed" -> articaltitle
# for example:
# <div class="headlines-li-div"><h1><a href="https://abcnews.go.com/Business/wireStory/stroke-luck-global-cruise-pandemic-70232983" data-analytics="cid=clicksource_4380645_2_heads_hero_live_headlines_hed" data-position="1" class="black-ln">'A stroke of luck' to be on cruise during pandemic</a></h1>
for link in soup.findAll('a'):
    if(link.get("data-analytics") == "cid=clicksource_4380645_2_heads_hero_live_headlines_hed"):
        fileUrl = "./ABCNewsArticle/"+link.string+".txt"
        story = []
        # News title
        story.append(link.string)
        # News Url
        story_url = link.get('href')
        story_response = request.urlopen(story_url, timeout=10)
        story_html = story_response.read()
        story_charset = chardet.detect(story_html)
        story_html = story_html.decode(str(story_charset["encoding"]))
        story_soup = BeautifulSoup(story_html, "html.parser")
        # print(story_soup)
        for child in story_soup.find('div', id="abcnews").findAll('p'):
            story.append(child.string)
        with open(fileUrl, 'w') as file_obj:
            file_obj.write(str(story))
        # print(story)
